/* eslint-disable no-unused-vars */
import React, { useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Axios from 'axios';
import { UserContext } from '../../../utils/UserContext';

// Importing the logout feature
import { logout } from '../../../components/logout';

// Importing styling
import '../style/studentHome.css';
// For the check box...idk man
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCheck } from "@fortawesome/free-solid-svg-icons";

function StudentHome()
{

    // Used to navigate around the web app
    const navigate = useNavigate();

    // Getting userType
    const userType = useContext(UserContext);
    // This is for testing CSS purposes(Delete once backend is working!)
    const test = [
        {
            name: 'event 1',
            time: '10am',
            location: 'Orladno',
            type: 'private'
        },
        {
            name: 'event 2',
            time: '5:00 pm',
            location: 'Gville',
            type: 'public'
        }
    ]

    // check boxes
    const [checkedPrivate, setCheckedPrivate] = useState(false);
    const [checkedPublic, setCheckedPublic] = useState(false);
    const [checkedRSO, setCheckedRSO] = useState(false);

    // Handles for each checkBox 
    // The '!' negates the current value, so !true = false and !false = true
    const handleCheckedPrivate = (event) =>
    {
        setCheckedPrivate(!event.target.checked);
        console.log(event);
    }
    const handleCheckedPublic = () =>
    {
        setCheckedPublic(!checkedPublic);
    }
    const handleCheckedRSO = () =>
    {
        setCheckedRSO(!checkedRSO);
    }

    // useEffect(() =>
    // {  
    //     console.log(checkedPrivate);
    // }, [checkedPrivate]);

    return(

        <div className="superAdminEvents">
        STUDENT HOME PAGE
        {/* Logs the user out*/}
        <div>
            <button className="button" onClick={ logout }>Logout</button>
        </div>
        <div className="checkBoxes">
            <input type="checkbox" checked={ checkedPrivate } onChange={ handleCheckedPrivate }/>
            <span className="privateCheckMark">
                { checkedPrivate && <FontAwesomeIcon icon={ faCheck }/> }
                Private Events
            </span>
            <input type="checkbox" checked={ checkedPublic } onChange={ handleCheckedPublic }/>
            <span className="privateCheckMark">
                { checkedPublic && <FontAwesomeIcon icon={ faCheck }/> }
                Private Events
            </span>
            <input type="checkbox" checked={ checkedPrivate } onChange={ handleCheckedRSO }/>
            <span className="privateCheckMark">
                { checkedRSO && <FontAwesomeIcon icon={ faCheck }/> }
                Private Events
            </span>
            
        </div>
            <div className="eventTableWrapper">
                <div className="eventTable">
                    <table>
                        <thead>
                            <tr>
                                <th>Event Name</th>
                                <th>Time</th>
                                <th>Location</th>
                                <th>Private/Public/Rso</th>
                            </tr>
                        </thead>
                        <tbody>
                            {test.map( event => (
                                <tr key ={test.name}>
                                    <td>{event.name}</td>
                                    <td>{event.time}</td>
                                    <td>{event.location}</td>
                                    <td>{event.type}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>

        </div>
    );
}

export default StudentHome;
