/* eslint-disable no-unused-vars */
import React, { useEffect, useState } from 'react';
// import { useNavigate } from 'react-router-dom';
import Axios from 'axios';

// Import API's
import { handleCreatePublicEvent } from '../API/superAdmin/createPublicEvent';

function CreateUniversity({ showCreateUni, setShowCreateUni })
{
    // useState used to hold the public Event Info
    // The number of students is updated using a trigger in the SQL database
    const [eventInfo, setEventInfo] = useState({
        name: '',
        location: '',
        description: '',
    });

    // The Public Event information to be submitted to the backend
    const setData = (event) =>
    {
        const formData = new FormData(event.target);

        const eventData = {
            name: formData.get('name'),
            location: formData.get('location'),
            description: formData.get('description'),
        };
        
        // Set the eventInfo to the eventData we just got
        setEventInfo(eventData);
        // Call the API to send to backend
        handleCreatePublicEvent(eventData);
    }

    return(
        <div>Create Uni Page</div>
    )
}

export default CreateUniversity;
