import React, { useState, useEffect } from 'react';
import Axios from 'axios';
import './App.css';

// Importing the pages
import Register from './pages/register';
import Login from './pages/login';
import Events from './pages/events';
// import Rso from './pages/RSO';

// Import the logout function
import { logout } from './pages/ops/logout';

function App() 
{    

  const [loginStatus, setLoginStatus] = useState('');

  // Used to log the user in and create a cookie to track the current user 
  useEffect(() => {
    Axios.get("http://localhost:3001/login").then((response) => {
      if (response.data.loggedIn === true)
      {
        setLoginStatus(response.data.user[0].username);
        console.log(`User ${response.data.user[0].username} is logged in.`);
      }
    });
  }, []);

  // Used to log the current user out and remove the cookie, stopping the tracking of info
  const handleLogout = () =>
  {
    logout(); // Call the logout function to clear cookies and redirect to login/register page
    setLoginStatus(''); // Clear the login status in the state
  }

  return (
    <div className='App'>
      {/*Only render Regist/Login if user is not authenticated!*/}
      {!loginStatus && (
        <>
            <Register setLoginStatus={setLoginStatus}/>
            <Login setLoginStatus={setLoginStatus}/>
        </>
      )}
      {/*Render Events if user is authenticated*/}
      {loginStatus && <Events loginStatus={loginStatus}/>}
    </div>
   );
}

export default App;
