        <div className='mainContainer'>
            STUDENT EVENT REVIEW PAGE
            <div className='navButtons'>
                <button onClick={logout}>Logout</button>
                <button onClick={() => navigate('/studentHome')}>Student Home</button>
            </div>
            <div className='tableWrapper'>
                <div>
                    <table className='contentTable'>
                        <thead>
                            <tr>
                                <th>Rating</th>
                                <th>Review</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {reviews.map((val) => (
                                <tr key={val.commentId}>
                                    <td>{val.rating === 1 ? '1 Start' : val.rating + ' Stars'}</td>
                                    <td>{val.review}</td>
                                    <td>
                                        <button>Edit</button>
                                        <br></br>
                                        <button>Delete</button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>

            </div>
        </div>

