import React, { useState } from 'react';
import Axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Register() 
{
    // Used to navigate to the login page
    const navigate = useNavigate();

    // Info Used for the Registering of a new user
    const [usernameReg, setUsernameReg] = useState('');
    const [passwordReg, setPasswordReg ] = useState('');
    const [emailReg, setEmailReg ] = useState('');

    // Used to register a new student account
    const studentRegister = () => 
    {
        Axios.post('http://localhost:3001/register', {
        username: usernameReg,
        password: passwordReg,
        email: emailReg,
        userType: 'student',
        }).then(() => {
        alert('Successful insert');
        })
        .catch((error) =>
        {
          alert(error.message);
        });
    };

    // Used to register a new superadmin account
    const superAdminRegister = () => 
    {
        Axios.post('http://localhost:3001/registerSuperAdmin', {
        username: usernameReg,
        password: passwordReg,
        email: emailReg,
        userType: 'superadmin',
        }).then(() => {
        alert('Successful insert');
        });
    };

    // HTML FOR THE FILE 
    return (
          <div className='registration'>
          <h1>REGISTER</h1>
            <div>
              <label>Username:</label>
              <input type="text"
              onChange={(e) => {
                setUsernameReg(e.target.value);
                }}></input>
            </div>
            <div>
              <label>Email:</label>
              <input 
              type="text"
              onChange={(e) => {
                setEmailReg(e.target.value);
                }}></input>
            </div>
            <div>
              <label>Password:</label>
              <input 
              type="text"
              onChange={(e) => {
                setPasswordReg(e.target.value);
                }}></input>
            </div>
            {/* <div>
              <label>User Type:</label>
              <select name="userType" onChange={(e) =>{
                setUserType(e.target.value);
                }}>
                <option>Select a User Type</option>
                <option value="superAdmin">Super Admin (1 per University)</option>
                <option value="student">Student</option>
              </select>
            </div> */}
            <div>
              <button onClick={ studentRegister }>Register as Student</button>
              <button onClick={ superAdminRegister }>Register as SuperAdmin</button>
            </div>
            <button onClick={()=>navigate('/')}>To Login Page</button>
          </div>
    );
}

export default Register;
