/* eslint-disable no-unused-vars */
import React, { Fragment, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Axios from 'axios';

// Importing styling
import '../../style/editReview.css';

// Import utils
import ReadOnlyRow from '../utils/readOnlyRow'
import EditTableRow from '../utils/editTableRows';

// Logout feature
import { logout } from '../../../../components/logout';

function EditReviews()
{
    // Used to navigate around the web app
    const navigate = useNavigate();

    // Holds the users current reviews for all events
    const [reviews, setReviews] = useState([]);

    // Holds the updated rate/review
    const [newData, setNewData] = useState([]);

    const [editId, setEditId] = useState(null);

    // Getting the userId so we can retrieve the correct events
    const userId = sessionStorage.getItem('id');

    // Getting the information from the backend for all reviews
    // that belong to the eventId we are reviewing
    useEffect(() => 
    {
        Axios.get(`http://localhost:3001/studentHome/editReviews?userId=${userId}`)
            .then((response) =>
            {
                setReviews(response.data);
            })
            .catch((error) =>
            {
                console.log(error);
            });
    }, [userId]);

    // Function to send which eventId we are updating along with
    // the info to have updated 
    const handleEditReview = (event, review) =>
    {
        event.preventDefault();
        setEditId(review.commentId);
    }

    // Cancel button, to revert to original review data
    const handleCancelClick = () =>
    {
        setEditId(null);
    }

    const handleReviewChange = (event) =>
    {
        event.preventDefault();

        Axios.post('http://localhost:30001/studentHome/editReview',
        {
            
        })
    }

    const handleDeleteReview = () =>
    {
        Axios.post('http://localhost:3001/studentHome/editReview',
        {

        })
    }

    return (
        <div className='mainContainer'>
            STUDENT EVENT REVIEW PAGE
            <div className='navButtons'>
                <button onClick={logout}>Logout</button>
                <button onClick={() => navigate('/studentHome')}>Student Home</button>
            </div>
            <div className='tableWrapper'>
                <div>
                        <table className='contentTable'>
                            <thead>
                                <tr>
                                    <th>Rating</th>
                                    <th>Review</th>
                                    <th>Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {reviews.map((review) => (
                                    <Fragment>
                                        { editId === review.commentId ? (
                                             <EditTableRow 
                                                review={ review }
                                                handleCancelClick={ handleCancelClick }
                                            /> 
                                            ) : (
                                             <ReadOnlyRow 
                                                review={ review }
                                                handleEditReview={ handleEditReview }/>
                                            )
                                        }
                                    </Fragment>
                                ))}
                            </tbody>
                        </table>
                </div>
            </div>
        </div>

    )
}

export default EditReviews;
