import React, { useState } from 'react';
import Axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Register.css';


function Register() 
{
    // Used to navigate to the login page
    const navigate = useNavigate();

    // Info Used for the Registering of a new user
    const [usernameReg, setUsernameReg] = useState('');
    const [passwordReg, setPasswordReg ] = useState('');
    const [emailReg, setEmailReg ] = useState('');
    const [userType, setUserType] = useState('');

    // Used to register a new User
    const register = () => {
        Axios.post('http://localhost:3001/register', {
        username: usernameReg,
        password: passwordReg,
        email: emailReg,
        userType: userType,
        }).then(() => {
        alert('Successful insert');
        });
    };

    // HTML FOR THE FILE 
    return (
      <div className='registration' style={{ background: 'linear-gradient(to bottom right, #A770EF, #CF8BF3)' }}>
        <h1>REGISTER</h1>
        <div>
          <label><i className="fas fa-user"></i></label>
          <input type="text" placeholder="Username" onChange={(e) => setUsernameReg(e.target.value)} />
        </div>
        <div>
          <label><i className="fas fa-envelope"></i></label>
          <input type="text" placeholder="Email" onChange={(e) => setEmailReg(e.target.value)} />
        </div>
        <div>
          <label><i className="fas fa-lock"></i></label>
          <input type="text" placeholder="Password" onChange={(e) => setPasswordReg(e.target.value)} />
        </div>
        <div>
          <label>User Type:</label>
          <select name="userType" onChange={(e) => setUserType(e.target.value)}>
            <option value="">Select a User Type</option>
            <option value="superAdmin">Super Admin</option>
            <option value="admin">Admin</option>
            <option value="student">Student</option>
          </select>
        </div>
        <button onClick={() => navigate('/')}>Login Page</button>
        <button onClick={register}>Register</button>
      </div>
    );
}

export default Register;
