import React, { useEffect, useState } from 'react';
// import { useNavigate } from 'react-router-dom';
import Axios from 'axios';

// Import components
import CreateEvent from './adminCreateComponents/createEvent';

function AdminCreateEvent()
{
    // Holds RSO lists that the admin can make an event for
    const [rsoList, setRsoList] = useState([]);
    // The current logged in User
    const userId = sessionStorage.getItem('id'); 
    // The information for the event that the admin is making
    const [eventInfo, setEventInfo] = useState({
        name: '',
        category: '',
        description: '',
        time: null,
        phone: null,
        email: null,
        isPrivate: null,
        hostRso: '',
        long: null,
        lat: null,
      });

    const handleSubmit = (event) =>
    {
        event.preventDefault();
        const form = event.target.elements;

        const updateEventInfo = 
        {
            name: form.name.name,
            category: form.name.category,
            description: form.name.description,
            time: form.name.time,
            phone: form.name.phone,
            email: form.name.email,
            isPrivate: form.name.isPrivate,
            hostRso: form.name.hostRso,
            long: form.name.long,
            lat: form.name.lat,
        };
        setEventInfo(updateEventInfo); // Updates the useState obj with the form info
    };

    useEffect(() =>
    {
        // Make a call to the backend to get the data
        Axios.get(`http://localhost:3001/mainPage/createEvent/getRso?userId=${userId}`)
        .then(response =>
            {
                setRsoList(response.data);
            })
            .catch(error =>
            {
                console.log(error);
            });
    }, []);

    return(
        <div className='mainContainer'>
            <h1>Creating New Event</h1>
            <CreateEvent rsoList={ rsoList } handleSubmit={ handleSubmit }/>
        </div>
    )
}

export default AdminCreateEvent;
