/* eslint-disable no-unused-vars */
import React, { useEffect, useState } from 'react';
// import { useNavigate } from 'react-router-dom';
import Axios from 'axios';

// Import API's
import { handleCreatePublicEvent } from '../API/superAdmin/createPublicEvent';

function CreatePublicEvent({ setShowCreatePublic, showCreatePublic })
{

    // The information for the event that the admin is making
    const [eventInfo, setEventInfo] = useState({
        name: '',
        category: '',
        description: '',
        time: null,
        phone: null,
        email: null,
        isApproved: 1,
        isPrivate: 1,
        hostRso: 'NONE',
        long: null,
        lat: null,
        rsoID: 2,
      });
    
    // The Public Event information to be submitted to the backend
    const setData = (event) =>
    {
        event.preventDefault();
        const formData = new FormData(event.target);

        // Sets the info to eventData so we can
        const eventData = 
        {
            name: formData.get('name'),
            category: formData.get('category'),
            description: formData.get('description'),
            time: formData.get('time'),
            phone: formData.get('phone'),
            email: formData.get('email'),
            isApproved: 1,
            isPrivate: 1,
            hostRso: formData.get('hostRso'),
            long: formData.get('long'),
            lat: formData.get('lat'),
        };

        // Update the object with the useState info
        setEventInfo(eventData);         
        handleCreatePublicEvent(eventInfo);
    }

    return(
        <div>Creating Public Event
            {showCreatePublic && (
                <form className='publicEvent' onSubmit={ setData }>
                    <button onClick={() => setShowCreatePublic(false)}>X</button>
                    <br></br>
                    <label>Event Name:
                        <input type='text' name='name'/>
                    </label>
                    <br></br>
                    <label>Category:
                        <input type='text' name='category'/>
                    </label>
                    <br></br>
                    <label>Description:
                        <textarea name='description'/>
                    <br></br>
                    </label>
                    <br></br>
                    <label>Date/Time:
                        <input type='datetime-local' name='time'/>
                    </label>
                    <br></br>
                    <label>Contact Number:
                        <input type='tel' name='phone'/>
                    </label>
                    <br></br>
                    <label>Contact Email:
                        <input type='email' name='email'/>
                    </label>
                    <br></br>
                    <label>Longitude:
                        <input type='number' step='any' name='long'/>
                    </label>
                    <br></br>
                    <label>Latitude:
                        <input type='number' step='any' name='lat'/>
                    </label>
                    <br></br>
                    <button type='submit'>Submit</button>
                </form>
            )}  

        </div>
    )
}

export default CreatePublicEvent;
