import React from 'react';

function ViewOptions({ viewOption, setViewOption, setH2Tag })
{
    return(
        <>
            <form>
                <div className="viewContainer">
                    <h3>View Options</h3>
                    <select name="eventType" value={viewOption} onChange={(e) => {
                        setViewOption(e.target.value);
                        setH2Tag(e.target.value);
                    }}>
                        <option value="private">Private Events</option>
                        <option value="rso">RSO Events</option>
                        <option value="public">Public Events</option>
