import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import Axios from 'axios';
import './App.css';

// Importing the pages
import Register from './pages/register';
import Login from './pages/login';
import Events from './pages/events';
// import Rso from './pages/RSO';

function App() 
{    

  const [loginStatus, setLoginStatus] = useState('');

  // Used to log the user in and create a cookie to track the current user 
  useEffect(() => {
    Axios.get("http://localhost:3001/login").then((response) => {
      if (response.data.loggedIn === true)
      {
        setLoginStatus(response.data.user[0].username);
        console.log(`User ${ response.data.user[0].username } is logged in.`);
      }
    });
  }, []);

  return (
    <BrowserRouter>
      <div className='App'>
        {/*Only render Regist/Login if user is not authenticated!*/}
        {/* NEW CODE  */}
        <Routes>
          <Route exact path='/'>
            {/*Only Render Regist/login if user is auth*/}
            { !loginStatus && (
              <>
                <Register setLoginStatus={ setLoginStatus }/>
                <Login setLoginStatus={ setLoginStatus }/>
              </>
            )}
            {/* Redirect to /events if auth */}
            { loginStatus && <Events loginStatus={ loginStatus }/>}
          </Route>
          <Route path='/events'>
            {/* Render events if user is Auth */}
            { loginStatus && <Events loginStatus= { loginStatus }/>}
            { !loginStatus && <Navigate to='/' />}
          </Route>
        </Routes>
        {/* END NEW CODE */}
        {/* OLD CODE DELETE NEW TO RESTORE DEFAULTS */}
        {/* {!loginStatus && (
          <>
              <Register setLoginStatus={ setLoginStatus }/>
              <Login setLoginStatus={ setLoginStatus }/>
          </>
        )} */}
        {/*Render Events if user is authenticated*/}
        {/* {loginStatus && <Events loginStatus={ loginStatus }/>} */}
      </div>
    </BrowserRouter>
   );
}

export default App;
